function findObjectByName(obj, targetName, path = []) {
  for (let key in obj) {
    let newPath = [...path, key];
    if (key === targetName) {
      return { path: newPath, value: obj[key] };
    }
    if (typeof obj[key] === 'object' && obj[key] !== null) {
      let result = findObjectByName(obj[key], targetName, newPath);
      if (result) return result;
    }
  }
  return null;
}

