using System;

class Program
{
    static void Main()
    {
        double pi = CalculatePi();
        Console.WriteLine($"Calculated PI: {pi}");
    }

    static double CalculatePi()
    {
        double pi = 0;
        int iterations = 1000000;
        double sum = 0;

        for (int i = 0; i < iterations; i++)
        {
            double x = Math.Sin(i * Math.PI / iterations);
            sum += x * x;
        }

        pi = 4 * sum;
        return pi;
    }
}
